import React, { useState, useContext } from "react";
import { GlobalContext } from "../context/GlobalStats";

export const BorrowingLending = () => {
  const { addTransaction } = useContext(GlobalContext);
  const [text, setText] = useState("");
  const [amount, setAmount] = useState();
  const [type, setType] = useState("E");
  const [person, setPerson] = useState("Da");

  const onSubmit = (text, amount, type, person) => {
    const newTransaction = {
      id: Math.floor(Math.random() * 1000000),
      text,
      amount: parseInt(amount, 10),
      type: type,
      person: person,
    };

    addTransaction(newTransaction);

    setText("");
    setAmount("");
  };

  return (
    <div className="flex">
      <div className="alert shadow-lg mx-10 my-2 ">
        <div>
          <div className="">
            <div className="card-compact w-60 bg-red-300 shadow-xl rounded-lg">
              <div className="card-body">
                <h3 className="card-title">Name of the person</h3>
                <p>You need to pay this person xyz to settle up!</p>
              </div>
            </div>
          </div>
          <div className="">
            <div className="card-compact w-60 bg-lime-300 shadow-xl rounded-lg">
              <div className="card-body">
                <h3 className="card-title">Name of the person</h3>
                <p>You need to pay this person xyz to settle up!</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
