import React, { useState, useContext } from "react";
import { GlobalContext } from "../context/GlobalStats";

export const Newtransaction = () => {
  const { addTransaction } = useContext(GlobalContext);
  const [text, setText] = useState("");
  const [amount, setAmount] = useState();

  const onSubmit = (text, amount) => {
    const newTransaction = {
      id: Math.floor(Math.random() * 1000000),
      text,
      amount: parseInt(amount, 10),
    };

    addTransaction(newTransaction);

    setText("");
    setAmount("");
  };

  return (
    <div className="flex">
      <div className="alert shadow-lg mx-20 my-2">
        <div>
          <div className="">
            <h3 className="font-bold">Add New Transaction</h3>
            <div className="flex">
              <div className="mx-2">
                <input
                  id="desc"
                  type="text"
                  placeholder="Description"
                  className="flex-auto w-40 input  mx-2 input-sm"
                  value={text}
                  onChange={(e) => setText(e.target.value)}
                />
              </div>
              <div className="flex-auto w-40 mx-2">
                <input
                  id="money"
                  type="text"
                  placeholder="Amount in INR"
                  className=" input my-2 input-sm"
                  value={amount}
                  onChange={(e) => setAmount(e.target.value)}
                />

                <button
                  className="flex-auto w-20 btn btn-success btn-sm mx-2"
                  onClick={() => onSubmit(text, amount)}
                >
                  Add
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
