import React, { useState, useContext } from "react";
import { GlobalContext } from "../context/GlobalStats";

export const Newtransaction = () => {
  const { addTransaction } = useContext(GlobalContext);
  const [text, setText] = useState("");
  const [amount, setAmount] = useState(0);

  const onSubmit = e => {
    e.preventDefault();
    const newTransaction ={
      id: Math.floor(Math.random()*1000000),
      text,
      amount
    }

    addTransaction(newTransaction);
  }

  return (
    <div className="flex justify-center">
      <div className="alert shadow-lg mx-20 my-2">
        <div>
          <svg
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            className="stroke-info flex-shrink-0 w-6 h-6"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
            ></path>
          </svg>
          <div className="">
            <h3 className="font-bold">Add New Transaction</h3>
            <div className="mx-2 my-2">
              <input
                type="text"
                placeholder="Description"
                className=" input  mx-2 input-sm"
                value={text}
                onChange={(e) => setText(e.target.value)}
              />
              <input
                type="text"
                placeholder="Amount in INR"
                className=" input my-2 input-sm"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
              />

              <button className="flex-auto w-32 btn btn-success btn-sm mx-2" onClick= {() => onSubmit()}>
                Add
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
