import React, { useState, useContext } from "react";
import { GlobalContext } from "../context/GlobalStats";

export const BorrowingLending = () => {
  const { transactions } = useContext(GlobalContext);

  const totals = {};

  transactions.forEach((transaction) => {
    if (transaction.type === "B" || transaction.type === "L") {
      if (!totals[transaction.person]) {
        totals[transaction.person] = 0;
      }
      if (transaction.type === "B") {
        totals[transaction.person] += transaction.amount;
      }
      if (transaction.type === "L") {
        totals[transaction.person] -= transaction.amount;
      }
    }
  });

  const payBack = Object.keys(totals).filter(person => totals[person] > 0)
  const askforMoney = Object.keys(totals).filter(person => totals[person] < 0)
  return (
    <div className="flex">
      <div className="alert shadow-lg mx-10 my-2 ">
        <div>
          <div className="">
            <div>
              {Object.keys(askforMoney).map((person) => (
                <div className="card-compact w-60 bg-red-300 shadow-xl rounded-lg my-1 mx-1">
                <div className="card-body">
                  <p>
                    You need to pay {person} {totals[person]} to settle up!
                  </p>
                </div>
              </div>

              ))}
            </div>
          </div>
          <div className="">
            <div className="card-compact w-60 bg-lime-300 shadow-xl rounded-lg">
              <div className="card-body">
                <h3 className="card-title">Name of the person</h3>
                <p>You need to pay this person xyz to settle up!</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
