import React, { useState, useContext } from "react";
import { GlobalContext } from "../context/GlobalStats";

export const ExpenditureGifts = () => {
  const { transactions } = useContext(GlobalContext);

  const expenditure = {};
  const gifts = {};
  transactions.forEach((transaction) => {
    if (transaction.type === "exp") {
      if (!expenditure[transaction.text]) {
        expenditure[transaction.text] = transaction.amount;
      } else {
        expenditure[transaction.text] += transaction.amount;
      }
    }
    if (transaction.type === "gift") {
      if (!gifts[transaction.text]) {
        gifts[transaction.text] = transaction.amount;
      } else {
        gifts[transaction.text] += transaction.amount;
      }
    }
  });

  return (
    <div className="flex flex-wrap justify-center">
      <div className="sm:flex alert shadow-lg mx-10 my-2 justify-center w-full ">
        <div>
          <div className="sm:flex w-full">
            <div className="flex w-full justify-center">
              {Object.entries(gifts).map(([key, value]) => (
                <div className=" w-full bg-lime-300 shadow-md rounded-lg sm:w-1/2 md:w-1/3 lg:w-1/4 xl:w-1/5 px-2 py-1 mx-1 my-1">
                  <button className="btn btn-block no-animation">
                    {key}
                    <button className="btn btn-circle btn-outline btn-xs mx-2">
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        className="h-6 w-6"
                        fill="none"
                        viewBox="0 0 24 24"
                        stroke="grey"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth="2"
                          d="M6 18L18 6M6 6l12 12"
                        />
                      </svg>
                    </button>
                  </button>
                </div>
              ))}
            </div>

            <div className="flex w-full justify-center">
              {Object.entries(expenditure).map(([key, value]) => (
                <div className="card-compact w-full bg-red-300 shadow-md rounded-lg sm:w-1/2 md:w-1/3 lg:w-1/4 xl:w-1/5 px-2 py-1 mx-1 my-1">
                  <div className="card-body flex items-center justify-between">
                    <div className="avatar placeholder">
                      <div className="bg-neutral-focus text-neutral-content rounded-full h-8 w-8 flex items-center justify-center">
                        <span className="text-xs">{key}</span>
                      </div>
                    </div>
                    <p>You need to pay INR {value} to settle up!</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
