import React, { useState, useContext } from "react";
import { GlobalContext } from "../context/GlobalStats";

export const BorrowingLending = () => {
  const { addTransaction } = useContext(GlobalContext);
  const [text, setText] = useState("");
  const [amount, setAmount] = useState();
  const [type, setType] = useState("E");
  const [person, setPerson] = useState("Da");

  const onSubmit = (text, amount, type, person) => {
    const newTransaction = {
      id: Math.floor(Math.random() * 1000000),
      text,
      amount: parseInt(amount, 10),
      type: type,
      person: person,
    };

    addTransaction(newTransaction);

    setText("");
    setAmount("");
  };

  return (
    <div className="flex">
      <div className="alert shadow-lg mx-10 my-2 ">
        <div>
          <div className="">
            <h3 className="font-bold">Borrow</h3>
            <div className="stack">
              <div className="card shadow-md bg-primary text-primary-content">
                <div className="card-body">
                  <h2 className="card-title">Notification 1</h2>
                  <p>You have 3 unread messages. Tap here to see.</p>
                </div>
              </div>
              <div className="card shadow bg-primary text-primary-content">
                <div className="card-body">
                  <h2 className="card-title">Notification 2</h2>
                  <p>You have 3 unread messages. Tap here to see.</p>
                </div>
              </div>
              <div className="card shadow-sm bg-primary text-primary-content">
                <div className="card-body">
                  <h2 className="card-title">Notification 3</h2>
                  <p>You have 3 unread messages. Tap here to see.</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
