import React, { useState, useContext } from "react";
import { GlobalContext } from "../context/GlobalStats";

export const Newtransaction = () => {
  const { addTransaction } = useContext(GlobalContext);
  const [text, setText] = useState("");
  const [amount, setAmount] = useState();
  const [type, setType] = useState("E");

  const onSubmit = (text, amount) => {
    const newTransaction = {
      id: Math.floor(Math.random() * 1000000),
      text,
      amount: parseInt(amount, 10),
      type: type,
    };

    addTransaction(newTransaction);

    setText("");
    setAmount("");
  };

  return (
    <div className="flex">
      <div className="alert shadow-lg mx-10 my-2 ">
        <div>
          <div className="">
            <h3 className="font-bold">Add New Transaction</h3>
            <div className="grid grid-flow-row auto-rows-max">
              <div className="flex-auto w-full">
                <input
                  id="desc"
                  type="text"
                  placeholder="Description"
                  className="flex-auto w-full input my-1 input-sm"
                  value={text}
                  onChange={(e) => setText(e.target.value)}
                  required
                />
              </div>

              <div className="flex-auto w-full">
                <input
                  id="money"
                  type="text"
                  placeholder="Amount in INR"
                  className="flex-auto w-full input  my-1 input-sm"
                  value={amount}
                  onChange={(e) => setAmount(e.target.value)}
                />
              </div>

              <select
                class="select select-bordered select-sm w-full max-w-xs"
                value={type}
                onChange={(e) => setType(e.target.value)}
              >
                <option selected value="E">
                  Expenditure
                </option>
                <option value="B">Borrowed</option>
                <option value="L">Lent</option>
                <option value="G">Gift</option>
              </select>

              {type === "B" || type === "L" ? (
                <div>
                  <label htmlFor="second-dropdown">
                    Select a second option:
                  </label>
                  <select id="second-dropdown">
                    <option value="">-- Select --</option>
                    <option value="option1">Option 1</option>
                    <option value="option2">Option 2</option>
                    <option value="option3">Option 3</option>
                  </select>
                </div>
              ) : null}

              <div className=" w-full my-1">
                <button
                  className=" w-full btn btn-success btn-sm"
                  onClick={() => onSubmit(text, amount)}
                >
                  Add
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
