import React, { useState, useContext } from "react";
import { GlobalContext } from "../context/GlobalStats";

export const BorrowingLending = () => {
  const { transactions } = useContext(GlobalContext);

  const totals = {};

  transactions.forEach((transaction) => {
    if (transaction.type === "B" || transaction.type === "L") {
      if (!totals[transaction.person]) {
        totals[transaction.person] = 0;
      }
      if (transaction.type === "B") {
        totals[transaction.person] += transaction.amount;
      }
      if (transaction.type === "L") {
        totals[transaction.person] -= transaction.amount;
      }
    }
  });

  const { askforMoney, payBack } = Object.entries(totals).reduce(
    (acc, [key, value]) => {
      if (value < 0) {
        acc.askforMoney[key] = value;
      } else {
        acc.payBack[key] = value;
      }
      return acc;
    },
    { askforMoney: {}, payBack: {} }
  );

  return (
    <div className="flex">
      <div className="alert shadow-lg mx-10 my-2 ">
        <div>
          <div className="flex w-full">
            <div className="grid h-20 flex-grow card  place-items-center">
            <div className="">
                {Object.entries(askforMoney).map(([key, value]) => (
                  <div className="card-compact w-60 bg-lime-300 shadow-xl rounded-lg my-1">
                    <div className="card-body">
                      <p>
                        You need to ask {key} INR {-value} to settle up!
                      </p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
            <div className="divider divider-horizontal">OR</div>
            <div className="grid h-20 flex-grow card place-items-center">
              <div className="">
                {Object.entries(payBack).map(([key, value]) => (
                  <div className="card-compact w-60 bg-lime-300 shadow-xl rounded-lg">
                    <div className="card-body">
                      <p>
                        You need to pay {key} INR {value} to settle up!
                      </p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
